INSERT INTO `tbl_information_description` (`information_id`, `language_id`, `name`, `description`, `meta_keyword`, `meta_description`) VALUES
(3, 2, 'Background', '<p style="text-align: justify;">Thai Ambassador to the Kingdom of Cambodia, H.E. Mr. Doumdech Bunnark inspired Thai business persons in theKingdom to establish Thai Business Council of Cambodia to provide information and guidance to Thai investorsinterested in doing business and/or invest in Cambodia.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Thai Business Council of Cambodia also serves as communication channel between the embassy and Thai Peopleresiding in Cambodia on issues important and on emergency situation.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">The Thai Business Council of Cambodia was successfully established on July 22, 1999 through the great support fromn Mr. Samrerng Traiukhoat, Minister Counselor of Comercial Affairs at that time.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Prior to the creation of TBCC, there was a Thai investor group led by Bangkok Bank&rsquo;s Manager, Mr. Nikom, and theSecond Secretary of the Royal Thai Embassy, Mr. Dusith who are the key persons to established and draft the firstregulation of TBCC.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Under the first presidency of Mr. Somsak Rinruengsin, Modern Plastic &amp; Packaging Co., Ltd., TBCC had an office in hiscompany and later on during the term of Minister Counselor of Commercial Affairs, Mr. Bunnarith Kalayanmith, TBCCmoved to the Royal Thai Embassy up to today.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Thai Business Council of Cambodia changed its name to Thai Business Council in Cambodia since 2014. NowMrs.Oranooch Pakarat, Owner and Manager of Intra Co (Logistic Company in Cambodia) is the current TBCCpresident for the year 2016-2017.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>TBCCCommittees and Staffs2016-2017</strong></p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">See inname list inExcel Files&ldquo;Committee&rdquo; andorganize Chart as below</p>\r\n', NULL, NULL),
(4, 2, 'WHAT PEOPLE SAY', '<p><span style="font-size:16px">TBCC &ldquo;the center of Thai Business and Community in Cambodia&rdquo;</span></p>\r\n', NULL, NULL),
(5, 2, 'About TBCC Cambodia', '<p><span style="font-size:14px"><strong>Background</strong></span></p>\r\n\r\n<p>Thai Ambassador to the Kingdom of Cambodia, H.E. Mr. Doumdech Bunnark inspired Thai business persons in theKingdom to establish Thai Business Council of Cambodia to provide information and guidance to Thai investorsinterested in doing business and/or invest in Cambodia.</p>\r\n\r\n<p>Thai Business Council of Cambodia also serves as communication channel between the embassy and Thai Peopleresiding in Cambodia on issues important and on emergency situation.</p>\r\n\r\n<p>The Thai Business Council of Cambodia was successfully established on July 22, 1999 through the great support fromMr. Samrerng Traiukhoat, Minister Counselor of Comercial Affairs at that time.</p>\r\n\r\n<p>Prior to the creation of TBCC, there was a Thai investor group led by Bangkok Bank&rsquo;s Manager, Mr. Nikom, and theSecond Secretary of the Royal Thai Embassy, Mr. Dusith who are the key persons to established and draft the firstregulation of TBCC.</p>\r\n\r\n<p>Under the first presidency of Mr. Somsak Rinruengsin, Modern Plastic &amp; Packaging Co., Ltd., TBCC had an office in hiscompany and later on during the term of Minister Counselor of Commercial Affairs, Mr. Bunnarith Kalayanmith, TBCCmoved to the Royal Thai Embassy up to today.</p>\r\n\r\n<p>Thai Business Council of Cambodia changed its name to Thai Business Council in Cambodia since 2014. NowMrs.Oranooch Pakarat, Owner and Manager of Intra Co (Logistic Company in Cambodia) is the current TBCCpresident for the year 2016-2017.</p>\r\n\r\n<p><strong><span style="font-size:16px">TBCCCommittees and Staffs2016-2017</span></strong></p>\r\n\r\n<p>See inname list inExcel Files&ldquo;Committee&rdquo; andorganize Chart as below</p>\r\n\r\n<p>&nbsp;</p>\r\n', NULL, NULL),
(6, 2, 'Welcome to TBCC', '<p style="text-align: justify;">Welcome to the Thai Business Council in Cambodia or TBCC &ldquo;the center of Thai Business and Community in Cambodia&rdquo; . We are group of Thai Business person and Thai people who residing in Thailand and Cambodia. We provide information and guidance to Thai investors interested in doing business and/or invests in Cambodia. And we also serve as communication channel between the embassy and Thai People residing in Cambodia on issues important and on emergency situation. We are c ooperating and networking with many local and international partners, not only in Thailand and Cambodia but also from around the World, both government and private sectors.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n', NULL, NULL),
(7, 2, 'GET INTOUCH', '<p>And get the lastest information about our company news and offer</p>\r\n', NULL, NULL);
